CREATE DEFINER=`root`@`localhost` PROCEDURE `handle_interaction`(
IN userId int, IN songId int, IN isLike BOOLEAN
)
BEGIN
declare playCount int;
	IF ( SELECT exists (SELECT 1 from interactions where user_id = userId AND song_id = songId)) THEN
		SELECT  play_count into playCount from interactions where user_id = userId AND song_id = songId ;
        set playCount = playCount + 1;
        SET SQL_SAFE_UPDATES = 0;
        update interactions SET is_liked = isLike, play_count = playCount  where user_id = userId AND song_id = songId LIMIT 1;
    else
    	INSERT INTO interactions(user_id, song_id, is_liked) VALUES (userId, songId, isLike) ;
    end if ;
END ;;
